import React, { useState } from "react";
import axios from "axios";

const App = () => {
  const [weatherData, setWeatherData] = useState(null); // Use more descriptive variable names
  const [city, setCity] = useState("");
  const apiKey = "b6d718bdcb631d4ab95c8d8c90a98553"; // Replace with your API key
  const apiUrl = `https://api.openweathermap.org/data/2.5/weather`;

  const searchCity = () => {
    if (city.trim() === "") return; // Don't search if the input is empty

    axios
      .get(apiUrl, {
        params: {
          q: city,
          appid: apiKey,
        },
      })
      .then((response) => {
        setWeatherData(response.data);
        console.log(response.data);
      })
      .catch((error) => {
        console.error("Error fetching weather data:", error);
      });

    setCity(""); // Clear the input after searching
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      event.preventDefault();
      searchCity();
    }
  };

  return (
    <div className="app">
      <div className="search">
        <input
          value={city}
          onChange={(event) => setCity(event.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Enter City"
          type="text"
        />
      </div>
      {weatherData && ( // Render weather data if available
        <div className="container">
          <div className="top">
            <div className="location">
              <p>{weatherData.name}</p>
            </div>
            <div className="temp">
              <h1>{Math.round(weatherData.main.temp - 273.15)}°C</h1>{" "}
              {/* Convert Kelvin to Celsius */}
            </div>
            <div className="description">
              <p>{weatherData.weather[0].description}</p>
            </div>
          </div>
          <div className="bottom">
            <div className="feels">
              <p>{Math.round(weatherData.main.feels_like - 273.15)}°C</p>
              <p className="bold">Feels like</p>
            </div>
            <div className="humidity">
              <p>{weatherData.main.humidity}%</p>
              <p className="bold">Humidity</p>
            </div>
            <div className="wind">
              <p>{weatherData.wind.speed} KMPH</p>
              <p className="bold">Wind Speed</p>
            </div>
          </div>
        </div>
      )}

      <div class="footer">
        <p>&copy; 2023 Pritiyar Weather. All rights reserved.</p>
        <p>
          Created by <a href="https://pk042priyanshu-portfolio.netlify.app/" target="_blank">Priyanshu Katiyar</a>
        </p>
      </div>
    </div>
  );
};

export default App;
