import React, {useState} from 'react';
import axios from 'axios';
const App = () => {
  const [data, setData] = useState({});
  const [city, setCity] = useState('');
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=b6d718bdcb631d4ab95c8d8c90a98553`;

  const searchCity = (event)=>{
    if(event.key ==='Enter'){
      axios.get(url).then((response)=>{
        setData(response.data);
        console.log(response.data);
      })
    }
    setCity('');
  }
  return (
    <div className="app">
      <div className="search">
        <input  value={city} onChange={event => setCity(event.target.value)}  onKeyDown={event => {
    if (event.key === 'Enter') {
      event.preventDefault();
      searchCity();
    }
  }} placeholder='Enter City' type="text"/>
      </div>
      <div className="container">
        <div className="top">
          <div className="location">
            <p>Kanpur</p>
          </div>
          <div className="temp">
            <h1>36°C</h1>
          </div>
          <div className="discription">
            <p>Clouds</p>
          </div>
        </div>
        <div className="bottom">
          <div className="feels">
            <p>41°C</p>
            <p className="bold">Feels like</p>
          </div>
          <div className="humidity">
            <p>80%</p>
            <p className="bold">
              Humidity
            </p>
          </div>
          <div className="wind">
            <p>12 KMPH</p>
            <p className="bold">Wind Speed</p>
          </div>

        </div>
      </div>
    </div>
  )
}


export default App;
